const LastFm = () => {
  const [query, setQuery] = useState("");
  const [showShortlist, setShowShortlist] = useState(false);
  const [artistData, setArtistData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleClose = () => setShowShortlist(!showShortlist);

  const apiKey = import.meta.env.VITE_LAST_FM_API_KEY;

  const getLastfmData = async () => {
    setIsLoading(true);
    if (!query.trim()) return; // avoid empty searches
    try {
      const res = await fetch(
        `https://ws.audioscrobbler.com/2.0/?method=artist.search&artist=${encodeURIComponent(
          query
        )}&api_key=${apiKey}&format=json`
      );
      const data = await res.json();
      const cleanedData = data?.results?.artistmatches?.artist.map((item) => ({
        id: item.name.toLowerCase().replace(/\s+/g, "-"),
        name: item.name,
        listeners: item.listeners,
        imageUrl: item.image.find((img) => img["#text"])?.["#text"] || null,
      }));
      // console.log("API Response:", data?.results?.artistmatches?.artist);
      // console.log("API Response cleanedData:", cleanedData);
      setArtistData(cleanedData);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  return (
    <div className="px-10">
      {showShortlist ? (
        <Shortlist handleClose={handleClose} />
      ) : (
        <div>
          <Searchbar
            title={"Last.fm"}
            query={query}
            setQuery={setQuery}
            onSearch={getLastfmData}
          />
          {artistData.length > 0 ? (
            <>
              <div className="mt-20">
                <h1 className="text-3xl border-b-2 border-gray-300 pb-2">
                  Search Results:
                </h1>

                <div className="flex justify-end mt-2">
                  <button
                    className="border px-3 py-1 text-xs rounded-sm hover:bg-gray-100 transition cursor-pointer"
                    onClick={() => setShowShortlist(!showShortlist)}
                  >
                    Show short-list
                  </button>
                </div>
              </div>
              <ArtistTable data={artistData} />
            </>
          ) : (
            !isLoading && (
              <h1 className="text-xl text-gray-500 italic text-center mt-10">
                Start typing an artist name to explore their music releases 🎵
              </h1>
            )
          )}
          {isLoading && <Loader />}
        </div>
      )}
    </div>
  );
};

export default LastFm;
